// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.8.0;
pragma experimental ABIEncoderV2;

contract Strories {
    struct Story {
        uint256 id;
        string headline;
        string content;
        uint256 postedAt;
        bool exist;
    }
    mapping(uint256 => Story) private database;

    modifier storyExists(uint256 _id) {
        Story memory story = database[_id];
        require(story.exist, "Story not found");

        _;
    }

    function postStory(
        uint256 _id,
        string memory _headline,
        string memory _content,
        uint256 _postedAt
    ) external {
        database[_id] = Story(_id, _headline, _content, _postedAt, true);
    }

    function getStory(uint256 _id)
        external
        view
        storyExists(_id)
        returns (Story memory)
    {
        return database[_id];
    }
}
